"use client";

import KPSection from "@/components/ui/KPSection";
import KPRow from "@/components/ui/KPRow";
import { useEffect, useRef } from "react";
import KPHeading from "@/components/ui/KPHeading";
import KPParagraph from "@/components/ui/KPParagraph";
import Link from "next/link";
import { kpButtonVariants } from "@/components/ui/KPButton";

// Function to animate the SVG chart
const animateChart = () => {
  const path = document.getElementById("chartLine");
  const length = path.getTotalLength();
  const colorTransitionDuration = 1000; // Transition duration in milliseconds
  const initialColor = "#c1272d";
  const finalColor = "#3f3f4633";

  path.style.strokeDasharray = length;
  path.style.strokeDashoffset = length;

  // Animate the chart line
  path.animate([{ strokeDashoffset: length }, { strokeDashoffset: 0 }], {
    duration: 800,
    easing: "linear",
  }).onfinish = () => {
    // Transition the color gradually
    const colorKeyframes = [{ stroke: initialColor }, { stroke: finalColor }];

    const colorTiming = {
      duration: colorTransitionDuration,
      easing: "linear",
      fill: "forwards", // Maintain the final state of the animation
    };

    path.animate(colorKeyframes, colorTiming);
    path.style.strokeDasharray = "none"; // Remove dash array
    path.style.stroke = finalColor; // Set final color
  };
};

const animateBox = () => {
  const path = document.getElementById("lightbulbbox");
  const length = path.getTotalLength();
  const colorTransitionDuration = 1000; // Transition duration in milliseconds
  const initialColor = "white";
  const finalColor = "#3f3f4633";
  const animationDuration = 4000; // Total animation duration in milliseconds

  path.style.strokeDasharray = length;
  path.style.strokeDashoffset = length;

  // Animate the box line
  path.animate([{ strokeDashoffset: length }, { strokeDashoffset: 0 }], {
    duration: animationDuration,
    easing: "linear",
  }).onfinish = () => {
    // Transition the color gradually
    const colorKeyframes = [{ stroke: initialColor }, { stroke: finalColor }];

    const colorTiming = {
      duration: colorTransitionDuration,
      easing: "linear",
      fill: "forwards", // Maintain the final state of the animation
    };

    path.animate(colorKeyframes, colorTiming);
    path.style.strokeDasharray = "none"; // Remove dash array
    path.style.stroke = finalColor; // Set final color
  };
};

